@SuppressLint("RestrictedApi")
fun DrawScope.drawBehind(
    progress: Float,
    animation: Animatable<Float, AnimationVector1D>,
    isChecked: Boolean
) {
    scale(
        scaleX = (size.width - PADDING) / WIDTH_DRAWABLE_JAR,
        scaleY = (size.height - PADDING) / HEIGHT_DRAWABLE_JAR
    ) {
        translate(
            left = (size.width - WIDTH_DRAWABLE_JAR) * COEFFICIENT,
            top = (size.height - HEIGHT_DRAWABLE_JAR) * COEFFICIENT
        ) {
            if (isChecked) drawPath(
                PathParser
                    .createPathFromPathData(PATH_DATA_JAR)
                    .asComposePath(),
                color = Blue
            )
            else drawPath(
                PathParser
                    .createPathFromPathData(PATH_DATA_JAR)
                    .asComposePath(),
                color = Blue,
                style = Stroke(width = WIDTH)
            )
        }
    }
    scale(
        scaleX = ((size.width - PADDING) / WIDTH_DRAWABLE_JAR) * SCALE,
        scaleY = ((size.height -PADDING) / HEIGHT_DRAWABLE_JAR) * SCALE
    ) {
        scale(animation.value) {
            translate(
                left = (size.width - WIDTH_DRAWABLE_BRAIN) * COEFFICIENT,
                top = (size.height - HEIGHT_DRAWABLE_BRAIN) * COEFFICIENT
            ) {
                drawPath(
                    PathParser
                        .createPathFromPathData(PATH_DATA_BRAIN)
                        .asComposePath(),
                    brush = progress.toBrush()
                )
            }
        }
    }
}

fun CoroutineScope.brainAnimate(
    animation: Animatable<Float, AnimationVector1D>
) {
    this.launch(Dispatchers.IO) {
        var value by mutableStateOf(INITIAL_VALUE)
        var isScale by mutableStateOf(true)
        while (true) {
            if (isScale) {
                value += STEP
                animation.animateTo(
                    value,
                    animationSpec = spring(INITIAL_VALUE, Spring.StiffnessMedium)
                )
                if (value >= INITIAL_VALUE) isScale = false
            } else {
                value -= STEP
                animation.animateTo(
                    value,
                    animationSpec = spring(INITIAL_VALUE.minus(0.1f), Spring.StiffnessMedium)
                )
                if (value <= INITIAL_VALUE.minus(0.1f)) isScale = true
            }
        }
    }
}